import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUserActivities } from "../../redux/actions";

const Activities = () => {
  const dispatch = useDispatch();

  const activities = useSelector((state) => state.activities);

  useEffect(() => {
    (async () => {
      dispatch(getUserActivities());
    })();
  }, []);

  return (
    <div className="activities">
      <ul className="activities__list">
        {activities.map((el) => {
          return <ActivityCard activity={el} key={el.id} />;
        })}
      </ul>
    </div>
  );
};

const ActivityCard = (props) => {
  const { activity } = props;
  const { name, start_date, distance, elapsed_time, total_elevation_gain } =
    activity;

  return (
    <div className="card">
      <li>
        <h3 className="card__name">{name}</h3>
        <span className="card__start-date">
          Start date: <div>{start_date}</div>
        </span>
        <span className="card__distance">Distance: {distance}</span>
        <span className="card__elapsed-time">Elapsed time: {elapsed_time}</span>
        <span className="card__total-elevation-gain">
          Elevation gain: {total_elevation_gain}
        </span>
      </li>
    </div>
  );
};

export default Activities;
