import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUserActivities } from "../../redux/actions";

const Activities = () => {
  const dispatch = useDispatch();

  const activities = useSelector((state) => state.activities);

  useEffect(() => {
    (async () => {
      dispatch(getUserActivities());
    })();
  }, []);

  return (
    <div className="activities">
      <ul className="activities__list">
        {activities.map((el) => {
          <ActivityCard activity={el} />;
        })}
      </ul>
    </div>
  );
};

const ActivityCard = (props) => {
  const { activity } = props;
  const { id, name, start_date, distance, elapsed_time, total_elevation_gain } =
    activity;

  return (
    <li key={id}>
      <span className="name">{name}</span>
      <span className="start-date">{start_date}</span>
      <span className="distance">{distance}</span>
      <span className="elapsed-time">{elapsed_time}</span>
      <span className="total-elevation-gain">{total_elevation_gain}</span>
    </li>
  );
};

export default Activities;
