import axios from "axios";
require("dotenv").config();
const { REACT_APP_ACCESS_TOKEN } = process.env.local;
const base_url = "https://www.strava.com/api/v3";
const access_token = `access_token=${REACT_APP_ACCESS_TOKEN}`;
console.log(process.env.REACT_APP_ACCESS_TOKEN);

export const getUserActivities = () => async (dispatch) => {
  const url = `${base_url}/activities/?${access_token}`;
  const response = await axios.get(url);
  dispatch({
    type: "GET_USER_ACTIVITIES",
    payload: response,
  });
};
