import axios from "axios";
require("dotenv").config();
const { ACCESS_TOKEN } = process.env;
export const base_url = "https://www.strava.com/api/v3";
export const access_token = `access_token=${ACCESS_TOKEN}`;

export const getUserActivities = () => async (dispatch) => {
  const url = `${base_url}/activities/?${access_token}`;
  const response = await axios.get(url);
  dispatch({
    type: "GET_USER_ACTIVITIES",
    payload: response,
  });
};
